import React from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Form from 'react-bootstrap/Form';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Button from 'react-bootstrap/Button';
import FormControl from 'react-bootstrap/FormControl';
import 'bootstrap/dist/css/bootstrap.min.css';


class Menu extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      showMenu: false,
    };
  }
  render(){
    const handleShowMenu = () => this.setState({showMenu: true});
    const handleCloseMenu = () => this.setState({showMenu: false});

    return(
      <>
        <Navbar.Toggle onClick={handleShowMenu} />
        <Offcanvas backdrop="false" placement="end" show={this.state.showMenu} onHide={handleCloseMenu}>
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Offcanvas</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            content 2
          </Offcanvas.Body>
        </Offcanvas>
      </>
    );
  }
}

export default Menu;
