import React from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form'
import 'bootstrap/dist/css/bootstrap.min.css';


class Auth extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      showAuth: false,
    };
  }
  render(){
    const handleShowAuth = () => this.setState({showAuth: true});
    const handleCloseAuth = () => this.setState({showAuth: false});
    return(
      <>
      <div className="d-grid gap-2">
        <Button onClick={handleShowAuth}>Войти <img className="img-fluid" src={this.props.login_img} height="25" width="25" alt=""/></Button>
      </div>
      <Modal
      show={this.state.showAuth}
      onHide={handleCloseAuth}
      backdrop='static'
      fullscreen="sm-down"
      >
        <Modal.Header closeButton>
          <Modal.Title>Войти в личный кабинет</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form name='Auth'>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Логин/Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="mail@example.com"
                autoFocus
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Пароль</Form.Label>
              <Form.Control
                type="password"
                placeholder="Введите пароль"
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseAuth}>
            Отмена
          </Button>
          <Button variant="primary" onClick={handleCloseAuth}>
            Войти
          </Button>
        </Modal.Footer>
      </Modal>
      </>
    );
  }
}

