import React from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Form from 'react-bootstrap/Form';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Button from 'react-bootstrap/Button';
import FormControl from 'react-bootstrap/FormControl';
import 'bootstrap/dist/css/bootstrap.min.css';

function Example() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Launch
      </Button>

      <Offcanvas show={show} onHide={handleClose}>
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Offcanvas</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          Some text as placeholder. In real life you can have the elements you
          have chosen. Like, text, images, lists, etc.
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
}

render(<Example />);

class Header extends React.Component {
  render(){
    //SETTINGS
    const search = 'Поиск';
    const link = 'Ссылка';
    //PROPS
    // logo = "./content/main/cherry.svg";
    // id = 'header'
    // market_name = 'Smokey Cherry'

    return(
      <Navbar key={this.props.id} variant="dark" bg="dark" expand={this.props.id} className="mb-3">
      <Container fluid>
        <Navbar.Brand href="#"><img className="img-fluid" src={this.props.logo} height="35" width="35"/>{this.props.market_name}</Navbar.Brand>
        <Button variant="warning" className="offset-md-8">{search}</Button>
        <Navbar.Toggle aria-controls={`offcanvasNavbar-expand-${this.props.id}`} />
        <Navbar.Offcanvas
          id={`offcanvasNavbar-expand-${this.props.id}`}
          aria-labelledby={`offcanvasNavbarLabel-expand-${this.props.id}`}
          placement="end"
          backdrop="false"
        >
          <Offcanvas.Header closeButton>
            <Offcanvas.Title id={`offcanvasNavbarLabel-expand-${this.props.id}`}>
              Меню
            </Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <Nav className="justify-content-end flex-grow-1 pe-3">
            <Form className="d-flex">
              <FormControl
                type="search"
                placeholder={search}
                className="me-2"
                aria-label="Search"
              />
              <Button variant="outline-success">{search}</Button>
            </Form>
              <Nav.Link href="#action1">{link}</Nav.Link>
              <Nav.Link href="#action2">{link}</Nav.Link>
              <NavDropdown
                title="Подменю"
                id={`offcanvasNavbarDropdown-expand-${this.props.id}`}
              >
                <NavDropdown.Item href="#action3">{link}</NavDropdown.Item>
                <NavDropdown.Item href="#action4">
                  {link}
                </NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="#action5">
                  {link}
                </NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Offcanvas.Body>
        </Navbar.Offcanvas>
      </Container>
    </Navbar>
    );
  }
}

export default Header;
