import React from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form'
import Auth from './auth.js'
import 'bootstrap/dist/css/bootstrap.min.css';

class AuthValidation extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      validated: false,
    };
  }
  render(){
    const userName = 'admin';
    const userPassword = '123';
    const valid = () => {
      const form = document.forms.Auth;
      const inputUserName = form.elements.User.value;
      const inputUserPassword = form.elements.Password.value;
      console.log('User: ' + inputUserName);
      console.log('Password: ' + inputUserPassword);
      if (userName === inputUserName) {
        console.log('Login valid');
        if (userPassword === inputUserPassword) {
          console.log('Password valid');
          this.Auth.handleCloseAuth;
        } else {
          console.log('Password invalid')
        }
      } else {
        console.log('Login invalid');
        if (userPassword === inputUserPassword) {
          console.log('Password valid');
        } else {
          console.log('Password invalid');
        }
      }

    };
    return(
      <>
      <Button variant="primary" onClick={valid}>
        Войти
      </Button>
      </>
    );
  }
}

export default AuthValidation;
