import React, {useState} from 'react';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

function Hook() {
  const [showSearch, setShowSearch] = useState(false);
  const handleCloseSearch = () => setShowSearch(false);
  const handleShowSearch = () => setShowSearch(true);
}

class Search extends React.Component {
  render(){

    return (
      <>
        <Button variant="warning" onClick={setShowSearch(true)} className="offset-md-8">
        <img className="img-fluid" src="#" height="35" width="35" alt=""/>
        </Button>

        <Offcanvas placement="top" show={Hook.showSearch} onHide={Hook.handleCloseSearch}>
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>12</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            content 1
          </Offcanvas.Body>
        </Offcanvas>
      </>
    );
  }

  }

export default Search;
