import React from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


class Menu extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      showMenu: false,
      showSubMenu: false,
    };
  }
  render(){
    const handleShowMenu = () => this.setState({showMenu: true});
    const handleCloseMenu = () => this.setState({showMenu: false});
    const handleShowSubMenu = () =>
    this.setState({
      showSubMenu: true,
      showMenu: false,
    });
    const handleCloseSubMenu = () => this.setState({
      showSubMenu: false,
      showMenu: true,
    });

    return(
      <>
        <Navbar.Toggle onClick={handleShowMenu} />
        <Offcanvas backdrop="false" placement="end" show={this.state.showMenu} onHide={handleCloseMenu}>
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Меню</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <Button onClick={handleShowSubMenu}>Каталог =></Button>
          </Offcanvas.Body>
        </Offcanvas>


        <Offcanvas backdrop="false" placement="end" show={this.state.showSubMenu} onHide={handleCloseSubMenu}>
          <Offcanvas.Header>
            <Offcanvas.Title>Каталог</Offcanvas.Title>
            <Button onClick={handleCloseSubMenu}>Назад</Button>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <Button>Категория 1</Button>
            <hr/>
            <Button>Категория 1</Button>
            <hr/>
            <Button>Категория 1</Button>
            <hr/>
            <Button>Категория 1</Button>
          </Offcanvas.Body>
        </Offcanvas>
      </>
    );
  }
}

export default Menu;
