import React from 'react';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as lupa from './search.json';

class Search extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      showSearch: false,
    };
  }
  render(){
    const handleShowSearch = () => this.setState({showSearch: true});
    const handleCloseSearch = () => this.setState({showSearch: false});

    return (
      <>
        <Button variant="light" onClick={handleShowSearch} className="offset-md-8">
        <img className="img-fluid" src={this.props.search_img} height="35" width="35" alt=""/>
        </Button>

        <Offcanvas placement="top" show={this.state.showSearch} onHide={handleCloseSearch}>
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Поиск по каталогу</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>

          <Form className="d-flex">
              <FormControl
                type="search"
                placeholder="Search"
                className="me-2"
                aria-label="Search"
              />
              <Button variant="outline-success">Search</Button>
            </Form>

          </Offcanvas.Body>
        </Offcanvas>
      </>
    );
  }
  }

export default Search;
