import 'bootstrap/dist/css/bootstrap.min.css';

class Search extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      showSearch: false,
    };
  }
  render(){
    const handleShowSearch = () => this.setState({showSearch: true});
    const handleCloseSearch = () => this.setState({showSearch: false});

    return (
      <>
        <Button variant="warning" onClick={handleShowSearch} className="offset-md-8">
        Search<img className="img-fluid" src={this.props.search_img} height="35" width="35" alt=""/>
        </Button>

        <Offcanvas placement="top" show={this.state.showSearch} onHide={handleCloseSearch}>
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>12</Offcanvas.Title>
