import React from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';


class Menu extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      showMenu: false,
      showSubMenu: false,
    };
  }
  render(){
    const handleShowMenu = () => this.setState({showMenu: true});
    const handleCloseMenu = () => this.setState({showMenu: false});
    const handleShowSubMenu = () =>
    this.setState({
      showSubMenu: true,
      showMenu: false,
    });
    const handleCloseSubMenu = () => this.setState({
      showSubMenu: false,
      showMenu: true,
    });

    //STYLE
    const back_button = {
      backgroundColor: "white",
      borderRadius: "0.8em",
      border: "none"
    };
    const login_button = {
      margin: "1em"
    };
    return(
      <>
        <Navbar.Toggle onClick={handleShowMenu} />
        <Offcanvas backdrop="false" placement="end" show={this.state.showMenu} onHide={handleCloseMenu}>
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Меню</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <Button><img className="img-fluid" src={this.props.login_img} height="25" width="25" alt=""/>Войти в личный кабинет</Button>
            <hr/>
            <Button onClick={handleShowSubMenu}>Каталог =></Button>
          </Offcanvas.Body>
        </Offcanvas>


        <Offcanvas backdrop="false" placement="end" show={this.state.showSubMenu} onHide={handleCloseSubMenu}>
          <Offcanvas.Header>
            <Offcanvas.Title>Каталог</Offcanvas.Title>
            <button style={back_button} onClick={handleCloseSubMenu}>
              <img className="img-fluid" src={this.props.back_img} height="25" width="25" alt=""/>
            </button>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <Button>Категория 1</Button>
            <hr/>
            <Button>Категория 1</Button>
            <hr/>
            <Button>Категория 1</Button>
            <hr/>
            <Button>Категория 1</Button>
          </Offcanvas.Body>
        </Offcanvas>
      </>
    );
  }
}

export default Menu;
