import React from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import 'bootstrap/dist/css/bootstrap.min.css';


class Auth extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      showAuth: false,
    };
  }
  render(){
    const handleShowAuth = () => this.setState({showAuth: true});
    const handleCloseAuth = () => this.setState({showAuth: false});
    return(
      <>
      <Button onClick={handleShowAuth}>Войти <img className="img-fluid" src={this.props.login_img} height="25" width="25" alt=""/></Button>
      <Modal show={this.state.showAuth} onHide={handleCloseAuth}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseAuth}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCloseAuth}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      </>
    );
  }
}

export default Auth;
